import * as React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import {
    getTotalBasketCount,
    getTotalBasketPrice
} from 'selectors';
import { IBasketCard } from 'reducers/basketCard';

type BasketCardProps = IBasketCard

class BasketCard extends React.Component<BasketCardProps, {}> {
    render () {
        const {totalBasketCount, totalPrice} = this.props;
        return (
            <div className='cart'>
                <div className='dropdown'>
                    <Link
                        to='/basket'
                        id='dlabel'
                        className='btn btn-primary btn-block btn-lg'
                    >
                        <i className='fa fa-credit-card' />
                        <span>&nbsp;{totalBasketCount} item(s) = ${totalPrice}</span>
                    </Link>
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => ({
    totalBasketCount: getTotalBasketCount(state),
    totalPrice: getTotalBasketPrice(state)
})

export default connect(mapStateToProps, null)(BasketCard);