import * as React from 'react';
import { connect } from 'react-redux';
import { searchPhone } from 'actions';
import * as SearchStore from 'reducers/search';

type SearchProps = typeof SearchStore.actionCreators

class Search extends React.Component<SearchProps,{value}> {
    constructor(props) {
        super(props);
        this.state = {
            value: ''
        };
    }
    handleChange = (event) => {
        this.setState({
            value: event.target.value
        })
    }
    handleSubmit = (event) => {
        event.preventDefault();
        this.props.searchPhone(this.state.value);
    }
    render () {
        return (
            <div className='well blosd'>
                <h3 className='lead'>Quick shop</h3>
                <div className='input-group'>
                <form onSubmit={this.handleSubmit}>
                    <input
                    onChange={this.handleChange}
                    type='text'
                    className='form-control'
                    />
                </form>
                <span className='input-group-btn'>
                <button className='btn btn-default'>
                    <span className='glyphicon glyphicon-search'/>
                </button>
                </span>
                </div>
            </div>
        );
    }
}

const mapDispatchToProps = {
    searchPhone: SearchStore.actionCreators.searchPhone
}

export default connect(null, mapDispatchToProps)(Search);