import * as React from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import { hot } from 'react-hot-loader'
import Snowboards from './Snowboards';
import Snowboard from './Snowboard';
import About from './About';
import Layout from './Layout';
import Basket from 'containers/Basket';


class App extends React.Component {
    render() {
        return (
            <Layout>
                <Route exact path='/' component={Snowboards} />
                <Route exact path='/snowboards' component={Snowboards} />
                <Route path='/categories/:id' component={Snowboards} />
                <Route path='/about' component={About} />
                <Route path='/snowboard/:id' component={Snowboard} />
                <Route path='/basket' component={Basket} />
            </Layout>
        );
    }
}

export default hot(module)(App)