import * as R from 'ramda';
import {
    ADD_SNOWBOARD_TO_BASKET,
    REMOVE_SNOWBOARD_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes';

export class IBasket {
    snowboards: any;
    totalPrice: any;
}

export const actionCreator = {
    removeSnowboardFromBasket: (id) => (dispatch: any) => {},
    cleanBasket: () => (dispatch: any) => {},
    basketCheckout: (snowboards) => () => {}
}

const initialState = [];

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_SNOWBOARD_TO_BASKET:
            return R.append(payload, state);
        case REMOVE_SNOWBOARD_FROM_BASKET:
            return R.without(R.of(payload), state);
        case CLEAN_BASKET:
            return [];
        default:
            return state;
    }
}