import {FETCH_SNOWBOARD_BY_ID_SUCCESS} from 'actionTypes';
import * as R from 'ramda';
import { Reducer } from 'redux';
import {
    fetchSnowboardById
    ,addSnowboardToBasket
} from 'actions'

export const actionCreators = {
    fetchSnowboardById: fetchSnowboardById,
    addSnowboardToBasket: addSnowboardToBasket
}

export class ISnowboard {
    snowboard: any;
    id: string;
    appSettings: any;
}

const initialState = {
    id: null,
    snowboard: null
} as ISnowboard

export const reducer: Reducer<ISnowboard> = (state: ISnowboard = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_SNOWBOARD_BY_ID_SUCCESS:
            return R.merge(state, {
                id: R.prop('id', payload)
            })
        default: 
            return state; 
    }
}